*{{cite journal | author = Menges, E.S. | title = Population viability analyses in plants: challenges and opportunities |journal = Trends in Ecology & Evolution | year = 2000 | volume = 15 | pages= 51&ndash;56 | doi = 10.1016/S0169-5347(99)01763-2 | pmid=10652555 | issue = 2}}
*{{cite journal | author = Morris, W.F. , Hudgens, B.R., Moyle, L.C., Stinchcombe, J.R., and Bloch, P.L. | title = Population viability analysis in endangered species recovery plans: Past use and future improvements |journal = Ecological Applications | year = 2002 | volume = 12 | pages= 708&ndash;712 | doi = 10.1890/1051-0761(2002)012[0708:PVAIES]2.0.CO;2 | issn = 1051-0761 | issue = 3}}
