| align="center" | [[magenta]]
| {{color|magenta|(●)}}
| =
| align="center" | [[red]]
| {{color|red|(●)}}
|-
| align="center" | [[magenta]]
| {{color|magenta|(●)}}
| +
| align="center" | [[cyan]]
| {{color|cyan|(●)}}
| =
| align="center" | [[blue]]
| {{color|blue|(●)}}
|-
| align="center" | [[cyan]]
| {{color|cyan|(●)}}
