{{Infobox military installation | name = Fort of Our Lady of the Conception | ensign = | ensign_size = | native_name = | partof = <!-- for elements within a larger site --> | location = Hormuz Island | nearest_town = | country = Iran | image = Portuguese Castle (Hormuz).jpg | alt = | caption = The Portuguese Castle on [[Hormuz Island]] | image2 = Qeshm Portuguese Castle.jpg | alt2 = | caption2 = The Portuguese Castle in [[Qeshm]] | type = Fort | coordinates = <!-- {{Coord}} --> | gridref = | image_map = Hormuz fort-Correia.png | image_mapsize = | image_map_alt = | image_map_caption = The Portuguese Castle ([[Gaspar Correia]]-"''Lendas da Índia''", c. 1556) | pushpin_map = | pushpin_mapsize = | pushpin_map_alt = | pushpin_map_caption = | pushpin_relief = | pushpin_image = | pushpin_label = | pushpin_label_position = | pushpin_mark = | pushpin_marksize = | ownership = <!-- government department such as the MoD or the United States DoD --> | operator = <!-- where different from ownership such as the RAF or the USAF --> | controlledby = <!-- such as RAF Bomber Command or the Eighth Air Force --> | open_to_public = <!-- for out of use sites/sites with museums etc --> | site_other_label = <!-- for renaming "Other facilities" in infobox --> | site_other = <!-- for other sorts of facilities - radar types etc --> | site_area = <!-- area of site m2, km2 square mile etc --> | code = <!--facility/installation code, applies to US --> | built = {{Start date|1515}} | used = <!--{{End date|1946}} --> | builder = | materials = Stone | height = <!-- height of tallest part, not above sea level --> | length = <!-- for border fences or other DMZs --> | fate = <!--changed from demolished parameter--> | condition = | battles = | events = | current_commander = <!-- current commander --> | past_commanders = <!-- past notable commander(s) --> | garrison = <!-- such as the 25th Bombardment Group --> | occupants = <!-- squadrons only --> | website = | footnotes = <!-- catchall in case it's nee