{{Infobox person | honorific_prefix = | name = Leonardo S. Sarao, Sr. | honorific_suffix = | native_name = | native_name_lang = | image = <!-- just the name, without the File: or Image: prefix or enclosing [[brackets]] --> | image_size = | alt = | caption = | birth_name = Leonardo Salvador Sarao | birth_date = April 13, 1921 | birth_place = | death_date = {{Death date and age|2001|07|31|1921|04|13}} | death_place = | resting_place = | resting_place_coordinates = <!-- {{Coord|LAT|LONG|type:landmark|display=inline}} --> | monuments = | residence = [[Las Piñas]], [[Metro Manila]] | nationality = [[Filipino people|Filipino]] | other_names = Mang Nardo | ethnicity = | citizenship = Filipino | education = [[Elementary education]] | alma_mater = | occupation = Businessman and automotive designer | years_active = 1953&ndash;2001 | employer = | organization = [[Sarao Motors|Sarao Motors, Inc.]] | agent = | known_for = Designing and popularizing the [[jeepney]] | notable_works = | style = | influences = | influenced = | home_town = [[Imus]], [[Cavite]], [[Philippines]] | salary = | net_worth = <!-- Net worth should be supported with a citation from a reliable source --> | height = <!-- {{height|m=}} --> | weight = <!-- {{convert|weight in kg|kg|lb}} --> | television = | title = | term = | predecessor = | successor = | party = | movement = | opponents = | boards = | religion = <!-- Religion should be supported with a citation from a reliable source --> | denomination = <!-- Denomination should be supported with a citation from a reliable source --> | criminal_charge = <!-- Criminality parameters should be supported with citations from reliable sources --> | criminal_penalty = | criminal_status = | spouse = | partner = <!-- unmarried life partner; use ''Name (1950–present)'' --> | children = | parents = Zacarias Sarao and Maria Salvador<ref name=tofil/> | relatives = | callsign = | awards = [[TOFIL Award]] in 1991 for Entrepreneurship | signature = | signature_alt = | signature