_title= |journal=Parameters |volume= |series= |issue= |page= |pages=93–105 |at= |chapter= |location= |publisher= |editor1-first= |editor1-last= |editor1-link= |language= |id= |isbn= |issn= |oclc= |pmid= |pmc= |bibcode= |doi= |accessdate= 2011-10-02|url=http://www.carlisle.army.mil/usawc/parameters/Articles/98summer/shay.htm |archiveurl= |archivedate= |laysource= |laysummary= |laydate= |quote= |ref= |separator= |postscript= }}
