{{Other uses}} {{Use dmy dates|date=May 2011}} {{More footnotes|date=December 2011}} {{Infobox settlement <!-- See Template:Infobox settlement for additional fields and descriptions --> <!-- Basic info ----------------> |name =Roussillon<!-- at least one of the first two fields must be filled in --> |official_name = |other_name = |native_name = {{native name|ca|Rosselló}} |nickname = |settlement_type = Historical province |total_type = <!-- to set a non-standard label for total area and population rows --> |motto = |anthem = <!-- images and maps -----------> |image_skyline = |imagesize = |image_caption = |image_flag = Flag of Catalonia.svg |flag_size = |image_seal = |seal_size = |image_shield = Arms of Roussillon.svg |shield_size =75px |image_blank_emblem = |blank_emblem_type = |blank_emblem_size = |image_map =Roussillon.png |mapsize = 250px |map_caption = |image_map1 = |mapsize1 = |map_caption1 = |image_dot_map = |dot_mapsize = |dot_map_caption = |dot_x = |dot_y = |pushpin_map = <!-- the name of a location map as per http://en.wikipedia.org/wiki/Template:Location_map --> |pushpin_label_position = <!-- the position of the pushpin label: left, right, top, bottom, none --> |pushpin_map_caption = |pushpin_mapsize = <!-- Location ------------------> |subdivision_type = Country |subdivision_name = [[France]] |subdivision_type1 = Departament |subdivision_name1 = [[Pyrénées-Orientales]] |subdivision_type2 = |subdivision_name2 = <!-- Smaller parts (e.g. boroughs of a city) and seat of government -->Perpignan |seat_type = Largest settlement |seat = [[Perpignan]] |parts_type = |parts_style=<!-- =list (for list), coll (for collapsed list), para (for paragraph format) Default is list if up to 5 items, coll if more than 5--> |parts = <!-- parts text, or header for parts list --> |p1 = |p2 = |p3= |p4= |p5= |p6= |p7= |p8= |p9= |p10= <!-- etc. up to p50: for separate parts to be listed--> <!-- Politics -----------------> |government_footnotes = |government_type = |leader_title = |l