{{Infobox organization
| name                = Operation Sacred Trust
| type                = Veterans Service Organization
| founded_date        = October 2011
| tax_id              = 47-4321566
| purpose             = humanitarian
| location            = 9050 Pines Blvd, Suite 305, Pembroke Pines, Florida
| num_employees       = ~20
| homepage            = {{url|411veterans.com}}
