{{Infobox organization | name = NSW Education Standards Authority | image = | abbreviation = NESA | motto = | predecessor = [[Board of Studies, Teaching and Educational Standards]] | successor = | formation = 2017 | type = [[Government agency]] | headquarters = [[Sydney, New South Wales]], [[Australia]] | location = 117 Clarence Street, Sydney | coords = {{coord|-33.867039|151.204965|display=inline,title}} | region_served = [[New South Wales]], [[Australia]] | leader_title = President | leader_name = Tom Alegounarias | leader_title2 = Chief Executive | leader_name2 = David De Carvalho | affiliations = [[ACARA]] | budget = $122 million | num_staff = 280 | website = [http://educationstandards.nsw.edu.au/ educationstandards.nsw.edu.au]
