{{Infobox person | honorific_prefix = | name = Minyong Kim | honorific_suffix = | image = Minyong Kim with student.jpeg | image_size = | alt = | caption = Minyong Kim poses with a student | native_name = | native_name_lang = | birth_name = <!-- only use if different from name above --> | birth_date = {{birth based on age as of date |25|2015|10|15}} | birth_place = | baptised = <!-- will not display if birth_date is entered --> | disappeared_date = <!-- {{disappeared date and age|YYYY|MM|DD|YYYY|MM|DD}} (disappeared date then birth date) --> | disappeared_place = | disappeared_status = | death_date = <!-- {{death date and age|YYYY|MM|DD|YYYY|MM|DD}} (death date then birth date) --> | death_place = | death_cause = | body_discovered = | resting_place = | resting_place_coordinates = <!-- {{coord|LAT|LONG|type:landmark|display=inline}} --> | monuments = | residence = [[Champaign-Urbana]], [[Illinois]], United States | nationality = [[South Korea]] | other_names = Dragon | ethnicity = <!-- Ethnicity should be supported with a citation from a reliable source --> | citizenship = [[South Korea]] | education = | alma_mater = [[University of Illinois at Urbana-Champaign]] | occupation = [[Undergraduate student]] | years_active = | era = | employer = | organization = | agent = <!-- Discouraged in most cases, specifically when promotional, and requiring a reliable source --> | known_for = Impersonation of [[Kim Jong-un]] | notable_works = <!-- produces label "Notable work"; may be overridden by |credits=, which produces label "Notable credit(s)"; or by |works=, which produces label "Works" --> | style = | home_town = | salary = | net_worth = <!-- Net worth should be supported with a citation from a reliable source --> | height = <!-- "X cm", "X m" or "X ft Y in" plus optional reference (conversions are automatic) --> | weight = <!-- "X kg", "X lb" or "X st Y lb" plus optional reference (conversions are automatic) --> | television = | title = | term = | predecessor = | successor 