| second_pitch  = [[minor second|D{{music|b}}]]
| third_pitch   = [[augmented second|D{{music|sharp}}]]
| fourth_pitch  = [[major third|E]]
| fifth_pitch   = [[augmented fourth|F{{music|sharp}}]]
| sixth_pitch   = [[minor sixth|A{{music|b}}]]
| seventh_pitch = [[minor seventh|B{{music|b}}]]
| pitch_classes = 7
| modes         = [[Ascending melodic minor|I]], [[Dorian b2 scale|II]], [[Lydian augmented scale|III]], [[Acoustic scale|IV]], [[Aeolian dominant scale|V]], [[Half diminished scale|VI]], VII
| Forte number  = 7-34
| complement    = 5-34
