| composer = Deniz Akbulut<ref>{{cite web |last1=Farrell |first1=Reilly |title=CrossCode soundtrack coming this September |url=http://www.vgmonline.net/crosscode-soundtrack-coming-this-september/ |website=Video Game Music Online |accessdate=August 9, 2018}}</ref>
| released = September 20, 2018
| genre = [[Action role-playing game|Action role-playing]]
