{{Infobox water ride | name = Coal Cracker | logo = <!--Use ONLY the filename, not a full [[File:]] link--> | logo_width = <!--Must be expressed in pixels (px); default is 250px--> | image = <!--Use ONLY the filename, not a full [[File:]] link--> | image_width = <!--Must be expressed in pixels (px); default is 250px--> | caption = | location = Hersheypark | locationarticle = <!--Must not be linked.--> | section = [[Hersheypark#Kissing Tower Hill|Kissing Tower Hill]] | cost = | coordinates = <!--Should only be used if only at one park. Use {{coord|display=inline,title}}--> | status = Open | soft_opened = | opened = {{start date|1973}} | closed = <!--Use {{End date}} --> | previousattraction = <!--If there is an appropriate article, this should be linked.--> | replacement = <!--If there is an appropriate article, this should be linked.--> | type = Log flume | manufacturer = Arrow Development | designer = | model = | course = | lift = | height_ft = 55 | drop_ft = 49 | length_ft = 2000 | speed_mph = <!--Must be expressed in miles per hour and may contain only numeric characters.--> | duration = 3:30 | angle = 35 | capacity = | acceleration = <!--Expression in full form e.g. "X to Y mph (? to ? km/h) in Z seconds". --> | acceleration_from = <!--Initial speed in mph or km/h defaults to zero, only numeric characters--> | acceleration_mph = <!--Final speed in mph may contain only numeric characters--> | acceleration_km/h = <!--Final speed in km/h may contain only numeric characters--> | acceleration_in = <!--Number of seconds may contain words --> | gforce = | restriction_ft = <!--Must be expressed in feet and may contain only numeric characters.--> | restriction_in = <!--Must be expressed in inches and may contain only numeric characters.--> | restriction_cm = <!--Must be expressed in centimetres and may contain only numeric characters.--> | boats = <!--Must only contain numeric values--> | riders_per_row = <!--Must only contain numeric values--> | rows_per_boat = <!--Must