| rev7 = ''[[Times Colonist]]''
| rev7Score = {{Rating|3|5}}<ref name=Colonist>{{Cite news |newspaper=[[Times Colonist]]|location=Victoria|author=Adrian Chamberlain|date=August 26, 2012|title=Fringe review: She Has a Name inspired by true story|url=http://www.timescolonist.com/news/Fringe+review+Name+inspired+true+story/7145452/story.html|accessdate=September 11, 2012}}</ref>
