{{Refimprove|date=January 2015}} {{Infobox person | honorific_prefix = | name = Stephanie Roper | honorific_suffix = | image = | image_size = | alt = | caption = | native_name = | native_name_lang = | pronunciation = | birth_name = | birth_date = 1960 | birth_place = | baptised = <!-- will not display if birth_date is entered --> | disappeared_date = | disappeared_place = [[Prince George's County, Maryland]] | disappeared_status = | death_date = April 3, 1982 | death_place = | death_cause = Murder | body_discovered = | resting_place = | resting_place_coordinates = <!-- {{coord|LAT|LONG|type:landmark|display=inline}} --> | burial_place = <!-- may be used instead of resting_place and resting_place_coordinates (displays "Burial place" as label) --> | burial_coordinates = <!-- {{coord|LAT|LONG|type:landmark|display=inline}} --> | monuments = | residence = | nationality = American | other_names = | citizenship = | education = | alma_mater = [[Frostburg State University]], [[Frostburg, Maryland|Frostburg]], [[Allegany County, Maryland]] | occupation = | years_active = | era = | employer = | organization = | agent = <!-- Discouraged in most cases, specifically when promotional, and requiring a reliable source --> | known_for = | notable_works = <!-- produces label "Notable work"; may be overridden by |credits=, which produces label "Notable credit(s)"; or by |works=, which produces label "Works" --> | style = | home_town = [[Croom, Maryland|Croom]], Prince George's County, Maryland | salary = | net_worth = <!-- Net worth should be supported with a citation from a reliable source --> | height = <!-- "X cm", "X m" or "X ft Y in" plus optional reference (conversions are automatic) --> | weight = <!-- "X kg", "X lb" or "X st Y lb" plus optional reference (conversions are automatic) --> | television = | title = | term = | predecessor = | successor = | party = | movement = | opponents = | boards = | criminal_charge = <!-- Criminality parameters should be supported with citations