{{Infobox hospital <!-- All parameters and comments should be left intact for future editors --> <!-- All parameters are optional, but please copy the entire template --> <!-- Full documentation: http://en.wikipedia.org/wiki/Template:Infobox_hospital --> | name = Buntingford almshouses | org/group = <!-- org or group that owns/manages the hospital --> | logo = <!-- please conform to copyright --> | logo_size = <!-- logo size or width in pixels --> | image = Buntingford almshouses.jpg | image_size = 225 <!-- image size or width in pixels (225 matches default map_size) --> | alt = <!-- alternative text for image, see [[WP:ALT]] --> | caption = Buntingford Almshouses, with war memorial in the foreground | map_type = <!-- uses the [[Template:Location map]] format; defines value for {{{1}}} parameter; must have latitude and longitude if using this --> | relief = <!-- any non-blank value (yes, 1, etc.) will cause the template to display a relief map image, where available --> | map_size = <!-- map size or width in pixels (do not include "px"); default is 225 --> | map_alt = <!-- alternative text for map image, see WP:ALT for details --> | map_caption = <!-- a small caption under the map such as "Shown in region, country" --> | latitude = <!-- to display coordinates (or: latd / latm / lats / latNS); required if using map_type --> | longitude = <!-- to display coordinates (or: longd / longm / longs / longEW); required if using map_type --> | location = Buntingford | region = Hertfordshire | state = England | country = UK | coordinates = <!-- alternative to latitude/longitude; use {{coord}} template with 'display=inline, title' --> | healthcare = <!-- UK: NHS; AU/CA: Medicare; ELSE free-form text, e.g. Private --> | funding = Government | type = <!-- use: Community, District, General, District General, Teaching, Specialist --> | affiliation = <!-- medical school / university affiliations (medical or paramedical) --> | patron = <!-- the individual who acts as the hospital pat