{{Infobox book | name = The Tango Singer | title_orig = El cantor de tango | orig_lang_code = es | image = The Tango Singer.jpg| image_caption = | author = [[Tomás Eloy Martínez]] | translator = [[Anne McLean]] | country = Argentina | language = Spanish | published = {{plainlist| *2004 ([[Planeta Group|Planeta]]) (Spanish) *2004 ([[Bloomsbury USA]])}} | pages = 246 | isbn = 9504911986 }}
