{{Infobox settlement <!-- Basic info ---------------->| official_name = <big>Greater Kuala Lumpur</big> | other_name = | settlement_type = Metropolitan Area | image_skyline = Kuala Lumpur, Malaysia Astronaut Imagery.JPG | imagesize = | image_caption = A satellite view of Greater Kuala Lumpur | image_flag = | flag_size = | image_seal = | seal_size = | image_shield = | shield_size = | image_blank_emblem = | blank_emblem_size = | motto = <!-- images and maps -----------> | image_map = | mapsize = | pushpin_map = <!-- the name of a location map as per http://en.wikipedia.org/wiki/Template:Location_map --> | pushpin_label_position = <!-- the position of the pushpin label: left, right, top, bottom, none --> | pushpin_mapsize = <!-- Location ------------------> | pushpin_map_caption = | subdivision_type = Country | subdivision_name = {{Flagu|Malaysia}} | subdivision_type1 = | subdivision_type2 = | subdivision_type3 = | subdivision_name1 = | subdivision_name2 = | subdivision_name3 = <!-- Politics -----------------> | established_title = | established_date = | established_title2 = | established_date2 = | established_title3 = | established_date3 = <!-- Area ---------------------> | leader_title = | leader_name = | leader_title1 = | leader_name1 = | unit_pref = | area_footnotes = | area_magnitude = | area_total_km2 = 2,793.27 | area_total_sq_mi = | area_land_km2 = <!--See table @ Template:Infobox Settlement for details on automatic unit conversion--> | area_land_sq_mi = | area_water_km2 = | area_water_sq_mi = | area_water_percent = | area_urban_sq_mi = | area_metro_sq_mi = | area_blank1_title = | area_blank1_km2 = | area_blank1_sq_mi = <!-- Population -----------------------> | elevation_footnotes = <!--for references: use<ref> </ref> tags--> | elevation_m = | elevation_ft = <!-- Area/postal codes & others --------> | population_total = 7,200,000 | population_as_of = 2015 | population_footnotes = <ref>{{cite web|url=http://focusmalaysia.my/Assets/KL%20on%20track%20to%20megacit