{{Use Australian English|date=May 2014}} {{Use dmy dates|date=May 2014}} {{Infobox government agency | agency_name = Department of Primary Industries, Parks, Water and Environment | type = [[Government department]] | nativename = | nativename_a = | nativename_r = | logo = New Official Logo of Tasmania.jpg | logo_width = | logo_caption = | seal = | seal_width = | seal_caption = | picture = | picture_width = | picture_caption = | formed ={{Start date|2009|07|01|df=y}} | preceding1 = [[Department of Environment, Parks, Heritage and the Arts]] | preceding2 = Department of Primary Industries, Parks, Water and Environment <!-- (etc.) --> | dissolved = | superseding = | jurisdiction = [[Government of Tasmania]] | headquarters = | coordinates = <!--{{coord|LATITUDE|LONGITUDE|type:landmark_region:US|display=inline,title}}--> | motto = | employees = | budget = | prime_minister_name = | minister1_name = [[Jeremy Rockliff]] | minister1_pfo = Minister for Primary Industries and Water | minister2_name = [[Matthew Groom]] | minister2_pfo = Minister for Environment, Parks and Heritage <!-- (etc.) --> | deputyminister1_name = | deputyminister1_pfo = | deputyminister2_name = | deputyminister2_pfo = <!-- (etc.) --> | chief1_name = John Whittington | chief1_position = [[Departmental secretary|Secretary]] | chief2_name = | chief2_position = <!-- (etc.) --> | director_general = | agency_type = | parent_department = | parent_agency = | child1_agency = [[Tasmania Parks and Wildlife Service|Parks and Wildlife Service]] | child2_agency = Crown Land Services | child3_agency = EPA Division and Environment Protection Authority | child4_agency = [[Royal Tasmanian Botanical Gardens]] | child5_agency = Office of Racing Integrity | child6_agency = Inland Fisheries Service | child7_agency = Aboriginal Heritage Tasmania | child8_agency = Heritage Tasmania | child9_agency = | child10_agency = | child11_agency = | child12_agency = | keydocument1 = <!-- (etc.) --> | website = {{URL|dpipwe.tas.gov.au}} |