| caption1 =
| image2 = Carolingian territorial divisions, 855.png
| width2 = {{#expr: (120 * 497 / 594) round 0}}
| alt2 =
| caption2 =
| image3 = Carolingian territorial divisions, 870.png
| width3 = {{#expr: (120 * 497 / 594) round 0}}
| alt3 =
