{{Use dmy dates|date=August 2013}} {{Infobox person | honorific_prefix = | name = Lajos Thallóczy | honorific_suffix = | native_name = Thallóczy Lajos | native_name_lang = Hungarian | image = Thallóczy Lajos sírja.jpg | image_size = thumb | alt = | caption = Lajos Thallóczy's grave at the [[Kerepesi cemetery]] in Budapest | birth_name = | birth_date = {{Birth date|df=yes|1857|12|08}}{{Cref2|A}} | birth_place = [[Košice|Kassa]], [[Kingdom of Hungary (1526–1867)|Kingdom of Hungary]], [[Austrian Empire]] | death_date = {{Death date and age|df=yes|1916|12|01|1857|12|02}} | death_place = [[Herceghalom]], [[Austria-Hungary]] | death_cause = | body_discovered = | resting_place = [[Kerepesi cemetery]] in Budapest | resting_place_coordinates = {{coord|47|29|44.77|N|19|5|26.11|E|region:HU_type:landmark|display=inline}} | monuments = | residence = | nationality = [[Kingdom of Hungary|Hungarian]] | other_names = | citizenship = | education = | alma_mater = | occupation = | years_active = | employer = | organization = | agent = | known_for = | notable_works = | style = | influences = | influenced = | home_town = | salary = | net_worth = <!-- Net worth should be supported with a citation from a reliable source --> | height = <!-- {{height|m=}} --> | weight = <!-- {{convert|weight in kg|kg|lb}} --> | television = | title = | term = | predecessor = | successor = | party = | movement = | opponents = | boards = | criminal_charge = <!-- Criminality parameters should be supported with citations from reliable sources --> | criminal_penalty = | criminal_status = | spouse = | partner = | children = | parents = | relatives = | callsign = | signature = | signature_alt = | signature_size = | module = | module2 = | module3 = | module4 = | module5 = | module6 = | website = <!-- {{URL|Example.com}} --> | footnotes = | awards = {{plainlist | * [[File:Ord.Franz.Joseph-CAV.png|50px]] [[Order of Franz Joseph]] * [[File:Ord.Leopold.PNG|50px]] [[Order of Leopold (Austria)|Order of Leopold]]}} | box_w