{{For|the place of the same name in Greece|Little Wallachia (disambiguation){{!}}Little Wallachia}} {{Infobox settlement | name = Oltenia | native_name = | other_name = | settlement_type = [[Historical regions of Romania|Historical region of Romania]] | image_skyline = Valea Oltului-Judetul Valcea.JPG | image_alt = | image_caption = [[Olt Defile]], [[Vâlcea County]] | flag_alt = | image_seal = | seal_alt = | image_shield = Banatul Craiovei (Oltenia) - Stemă.png | shield_alt = | nickname = "Lesser Wallachia" | motto = | image_map = Map of Romania with Oltenia highlighted.svg | map_alt = | map_caption = Map of Romania with Oltenia highlighted | pushpin_map = | pushpin_label_position = | pushpin_map_alt = | pushpin_map_caption = | coordinates = {{coord|44|30|N|23|30|E|type:adm1st_dim:200000|display=inline,title}} | coor_pinpoint = | coordinates_footnotes = | subdivision_type = Country | subdivision_name = {{flag|Romania}} | subdivision_type1 = | subdivision_name1 = | subdivision_type2 = | subdivision_name2 = | subdivision_type3 = | subdivision_name3 = | established_title = | established_date = | founder = | seat_type =Largest city | seat =[[Craiova]] | government_footnotes = | leader_party = | leader_title = | leader_name = | unit_pref = Metric <!-- ALL fields with measurements have automatic unit conversion --> <!-- for references: use <ref> tags --> | area_footnotes = | area_urban_footnotes = <!-- <ref name="auto"> </ref> --> | area_rural_footnotes = <!-- <ref name="auto"/> --> | area_metro_footnotes = <!-- <ref name="auto"/> --> | area_magnitude = <!-- <ref name="auto"/> --> | area_note = | area_water_percent = | area_rank = | area_blank1_title = | area_blank2_title = <!-- square kilometers --> | area_total_km2 = 24095 | area_land_km2 = | area_water_km2 = | area_urban_km2 = | area_rural_km2 = | area_metro_km2 = | area_blank1_km2 = | area_blank2_km2 = <!-- hectares --> | area_total_ha = | area_land_ha = | area_water_ha = | area_urban_ha = | area_rural_ha = | area_met