{{Infobox settlement |official_name = Beltinci |other_name = |native_name = <!-- for cities whose native name is not in English --> |nickname = |settlement_type = <!--For Town or Village (Leave blank for the default City)--> |motto = |image_skyline = Cerkev sv. Ladislava v Beltincih.JPG |imagesize = 250px |image_caption = St. Ladislaus' Church in Beltinci |image_flag = |flag_size = |image_seal = |seal_size = |image_shield = |shield_size = |image_blank_emblem = |blank_emblem_type = |blank_emblem_size = |image_map = |mapsize = |map_caption = |image_map1 = |mapsize1 = |map_caption1 = |image_dot_map = |dot_mapsize = |dot_map_caption = |dot_x = |dot_y = |pushpin_map = Slovenia<!-- the name of a location map as per http://en.wikipedia.org/wiki/Template:Location_map --> |pushpin_label_position =bottom |pushpin_map_caption =Location in Slovenia |subdivision_type = Country |subdivision_name = [[Image:Flag of Slovenia.svg|25px]] [[Slovenia]] |subdivision_type1 = Traditional region |subdivision_name1 = [[Prekmurje]] |subdivision_type2 = [[Statistical regions of Slovenia|Statistical region]] |subdivision_name2 = [[Mura Statistical Region|Mura]] |subdivision_type3 = [[Municipalities of Slovenia|Municipality]] |subdivision_name3 = [[Municipality of Beltinci|Beltinci]] |subdivision_type4 = |subdivision_name4 = |leader_name = |leader_title1 = |leader_name1 = |leader_title2 = |leader_name2 = |leader_title3 = |leader_name3 = |leader_title4 = |leader_name4 = |established_title = |established_date = |established_title2 = |established_date2 = |established_title3 = |established_date3 = |area_magnitude = |area_footnotes = |area_total_km2 = 12.2 |area_land_km2 = |area_water_km2 = |area_total_sq_mi = |area_land_sq_mi = |area_water_sq_mi = |area_water_percent = |area_urban_km2 = |area_urban_sq_mi = |area_metro_km2 = |area_metro_sq_mi = |area_blank1_title = |area_blank1_km2 = |area_blank1_sq_mi = |population_as_of =2002 |population_footnotes = |population_note = |population_total =2378 |popul