{{more footnotes|date=February 2014}} {{Infobox person | honorific_prefix = | name = William Chomsky | honorific_suffix = | native_name = | native_name_lang = | image = | image_size = | alt = | caption = | birth_name = | birth_date = {{birth date|1896|1|15}} | birth_place = [[Kupil]], [[Volhynian Governorate]], [[Russian Empire]] (in present-day [[Ukraine]]) | death_date = {{death date and age|1977|7|19|1896|1|15}} | death_place = [[Philadelphia]], [[Pennsylvania]], [[United States]] | body_discovered = | resting_place = | resting_place_coordinates = <!-- {{Coord|LAT|LONG|type:landmark|display=inline}} --> | monuments = | residence = | nationality = American | other_names = | ethnicity = <!-- Ethnicity should be supported with a citation from a reliable source --> | citizenship = | education = | alma_mater = | occupation = Scholar of [[Hebrew]] | years_active = | employer = | organization = | agent = | known_for = | notable_works = | style = | influences = | influenced = | home_town = | salary = | net_worth = <!-- Net worth should be supported with a citation from a reliable source --> | height = <!-- {{height|m=}} --> | weight = <!-- {{convert|weight in kg|kg|lb}} --> | television = | title = | term = | predecessor = | successor = | party = | movement = | opponents = | boards = | religion = <!-- Religion should be supported with a citation from a reliable source --> | denomination = <!-- Denomination should be supported with a citation from a reliable source --> | criminal_charge = <!-- Criminality parameters should be supported with citations from reliable sources --> | criminal_penalty = | criminal_status = | partner = | children = [[Noam Chomsky]], David Chomsky | spouse = Elsie Simonofsky | relatives = [[Aviva Chomsky]] <small>(granddaughter)</small> | callsign = | awards = | signature = | signature_alt = | signature_size = | module = | module2 = | module3 = | module4 = | module5 = | module6 = | website = <!-- {{URL|www.example.com}} --> | footnotes = | box_wid