{{Infobox person | name = <!-- include middle initial, if not specified in birth_name --> | honorific_suffix = | native_name = | native_name_lang = | image = <!-- just the name, without the File: or Image: prefix or enclosing [[brackets]] --> | image_size = | alt = | caption = | birth_name = | birth_date ={{Birth date and age|1931|11|19}} | baptism_date = | birth_place = [[Chicago]], [[Illinois]], U.S. | disappeared_date = <!-- {{Disappeared date and age|YYYY|MM|DD|YYYY|MM|DD}} (disappeared date then birth date) --> | disappeared_place = | disappeared_status = | death_date = <!-- {{Death date and age|YYYY|MM|DD|YYYY|MM|DD}} (death date then birth date) --> | death_place = | death_cause = | body_discovered = | resting_place = | resting_place_coordinates = <!-- {{Coord|LAT|LONG|type:landmark|display=inline}} --> | monuments = | residence = [[Los Angeles]], [[California]] | nationality = | other_names = | citizenship = | education = | alma_mater ={{Plainlist| *Sumner School, Chicago *Key Clark Branch school (Chicago) * [[Austin High School (Chicago, Illinois)]], 1949 *[[Wright Junior College]] (Chicago) *[[Northwestern University School of Speech]]}} | occupation = Actor | years_active = 1949–present | employer = | organization = | agent = | known_for = | notable_works = | style = | home_town = | salary = | net_worth = <!-- Net worth should be supported with a citation from a reliable source --> | height = <!-- {{height|m=}} --> | weight = <!-- {{convert|weight in kg|kg|lb}} --> | television = | title = | term = | predecessor = | successor = | party = | movement = | opponents = | boards = | religion = <!-- Religion should be supported with a citation from a reliable source --> | denomination = <!-- Denomination should be supported with a citation from a reliable source --> | criminal_charge = <!-- Criminality parameters should be supported with citations from reliable sources --> | criminal_penalty = | criminal_status = | spouse = Joy (2nd wife) | partner = <!-- unmarr