{{Redirect|Tucson|other uses|Tucson (disambiguation)}} {{Use mdy dates|date=July 2017}} {{Infobox settlement | name = Tucson, Arizona | official_name = City of Tucson | settlement_type = City | nickname = "The Old Pueblo", "Optics Valley" | image_skyline = TucsonDerivative.png | imagesize = | image_caption = From upper left: Downtown Tucson skyline, [[Pima County Courthouse]], [[Old Main, University of Arizona]], [[Saguaro National Park]], [[St. Augustine Cathedral (Tucson)|St. Augustine Cathedral]], [[Santa Catalina Mountains]] | image_flag = Flag of Tucson, Arizona.png | image_seal = | seal_size = 82 px | image_map = File:Pima County Arizona Incorporated and Unincorporated areas Tucson Highlighted 0477000.svg | mapsize = | map_caption = Location of Tucson in Pima County, Arizona. | image_map1 = | mapsize1 = | map_caption1 = | pushpin_label = Tucson | pushpin_map = USA Arizona#USA | pushpin_map_caption = Location in Arizona | subdivision_type = [[List of sovereign states|Country]] | subdivision_type1 = [[U.S. state|State]] | subdivision_type2 = [[List of counties in Arizona|County]] | subdivision_name = United States | subdivision_name1 = [[Arizona]] | subdivision_name2 = [[Pima County, Arizona|Pima]] | government_type = [[Council-manager government]] | leader_title = Mayor | leader_name = [[Jonathan Rothschild]] ([[Democratic Party (United States)|D]]) | established_title = Incorporated | established_date = February 7, 1877<ref>{{cite web|url=https://www.tucsonaz.gov/apps/maps-and-records/annexations |title=Annexations &#124; Official website of the City of Tucson |publisher=Tucsonaz.gov |date= |accessdate=2017-04-21}}</ref> | area_magnitude = 1 E8 | area_total_km2 = 612.55 | area_total_sq_mi = 236.50 | area_land_km2 = 611.73 | area_land_sq_mi = 236.19 | area_water_km2 = 0.82 | area_water_sq_mi = 0.32 | elevation_ft = 2389 | elevation_m = 728 | population_as_of = [[2010 United States Census|2010]] | population_est = 535677 | pop_est_as_of = 2017 | pop_est_footnote