{{Infobox settlement <!--See the Table at Infobox Settlement for all fields and descriptions of usage--> <!-- Basic info ----------------> |name = San Pablo Huitzo |settlement_type =[[Municipalities of Mexico|Municipality]] and town |other_name = |native_name = <!-- for cities whose native name is not in English --> |nickname = |motto = <!-- images and maps -----------> |image_skyline =Iglesia de San Pablo Huitzo Oaxaca.jpg |imagesize =250px |image_caption =The church of San Pablo in San Pablo Huitzo |image_flag = |flag_size = |image_seal = |seal_size = |image_shield = |shield_size = |image_blank_emblem = |blank_emblem_type = |blank_emblem_size = |image_map = |mapsize = 200px |map_caption = Location of the municipality in Oaxaca |pushpin_map = Mexico<!-- the name of a location map as per http://en.wikipedia.org/wiki/Template:Location_map --> |pushpin_label_position = above |pushpin_mapsize = |pushpin_map_caption = Location in Mexico <!-- Location ------------------> |subdivision_type = Country |subdivision_name = {{flag|Mexico}} |subdivision_type1 = [[Administrative divisions of Mexico|State]] |subdivision_name1 = [[Oaxaca]] |subdivision_type2 = |subdivision_name2 = <!-- Politics -----------------> |government_footnotes = |government_type = |leader_title = |leader_name = |established_title = Settled |established_date = 1522 <!-- Area ---------------------> |area_magnitude = |unit_pref = |area_footnotes = |area_total_km2 = <!-- ALL fields dealing with a measurements are subject to automatic unit conversion--> |area_land_km2 = <!--See table @ Template:Infobox Settlement for details on automatic unit conversion--> |area_water_km2 = |area_total_sq_mi = |area_land_sq_mi = |area_water_sq_mi = |area_water_percent = |area_urban_km2 =63.8 |area_urban_sq_mi = |area_metro_km2 = |area_metro_sq_mi = |area_blank1_title = |area_blank1_km2 = |area_blank1_sq_mi = <!-- Population -----------------------> |population_as_of =2005 |population_footnotes = |population_note = |population_t