{{Infobox person
| name        = Sidi al-Mukhtar ibn Ahmad al-Kunti
| image       = <!-- just the filename, without the File: or Image: prefix or enclosing [[brackets]] -->
| alt         = 
| caption     = 
| birth_name  = 
| birth_date  = 1729<!-- {{Birth date and age|YYYY|MM|DD}} or {{Birth-date and age|Month DD, YYYY}} -->
| birth_place = 
| death_date  = 1811<!-- {{Death date and age|YYYY|MM|DD|YYYY|MM|DD}} or {{Death-date and age|Month DD, YYYY|Month DD, YYYY}} (death date then birth date) -->
| death_place = 
| nationality = Timbuktu
| other_names = 
| occupation  = Cleric
