{{Infobox U.S. legislation | shorttitle = Agricultural Act of 1948 | longtitle = An Act to authorize the Secretary of Agriculture to stabilize prices of agricultural commodities; to amend section 22 of the Agricultural Adjustment Act, reenacted by the Agricultural Marketing Agreement Act of 1937; and for other purposes. | acronym = | nickname = | enacted by = 80th | effective date = July 3, 1948 | public law url = http://legisworks.org/congress/80/publaw-897.pdf | cite public law = 80-897 | cite statutes at large = {{usstat|62|1247}} | acts amended = | acts repealed = | title amended = [[Title 7 of the United States Code|7 U.S.C.: Agriculture]] | sections created = <!--{{USC}} can be used--> | sections amended = Chapter 35 § 1281 | leghisturl = | introducedin = House | introducedbill = {{USbill|80|H.R.|6248}} | introducedby = | introduceddate = | committees = | passedbody1 = | passeddate1 = | passedvote1 = | passedbody2 = | passedas2 = <!-- used if the second body changes the name of the legislation --> | passeddate2 = | passedvote2 = | conferencedate = | passedbody3 = | passeddate3 = | passedvote3 = | agreedbody3 = <!-- used when the other body agrees without going into committee --> | agreeddate3 = <!-- used when the other body agrees without going into committee --> | agreedvote3 = <!-- used when the other body agrees without going into committee --> | agreedbody4 = <!-- used if agreedbody3 further amends legislation --> | agreeddate4 = <!-- used if agreedbody3 further amends legislation --> | agreedvote4 = <!-- used if agreedbody3 further amends legislation --> | passedbody4 = | passeddate4 = | passedvote4 = | signedpresident = [[Harry S. Truman]] | signeddate = July 3, 1948 | unsignedpresident = <!-- used when passed without presidential signing --> | unsigneddate = <!-- used when passed without presidential signing --> | vetoedpresident = <!-- used when passed by overriding presidential veto --> | vetoeddate = <!-- used when passed by overriding presidential v