{{Infobox person | honorific_prefix = M.D. | name = Steven Hayden Pollock | honorific_suffix = | image = Steven_Hayden_Pollock_1976.jpg | image_upright = | alt = | caption = | native_name = | native_name_lang = | pronunciation = | birth_date = {{Birth date|1947|8|12}}<ref name="BillionGraves">{{cite web|title=Grave Site of Dr. Steven Hayden Pollock (1947-1981)|url=https://billiongraves.com/grave/Steven-Hayden-Pollock/11006187/|website=BillionGraves|accessdate=2 June 2017|language=en}}</ref><ref name="familysearchBirth">{{cite web|title=Steven Hayden Pollock - Birth Event|url=https://familysearch.org/ark:/61903/1:1:V27Y-CVV|website=Family Search|accessdate=2 June 2017}}</ref> | birth_place = [[Los Angeles]], [[California]]<ref name="familysearchBirth" /> | disappeared_date = <!-- {{disappeared date and age|YYYY|MM|DD|YYYY|MM|DD}} (disappeared date then birth date) --> | disappeared_place = | disappeared_status = | death_date = {{Death date and age|1981|2|1|1947|8|12}}<ref name="BillionGraves" /><ref name="harpersBloodSpore">{{cite web|author1=Hamilton Morris|authorlink1=Hamilton Morris|title=Blood Spore: Of Murder and mushrooms|url=https://harpers.org/archive/2013/07/blood-spore/|website=Harpers|accessdate=2 June 2017|language=en|date=July 2013}}</ref> | death_place = [[San Antonio, TX]]<ref name="harpersBloodSpore" /> | death_cause = Murder | body_discovered = | resting_place = Mission Burial Park North, San Antonio, Texas | resting_place_coordinates = {{Coord|29.635249|-98.610142|type:landmark|display=inline}} <ref name="BillionGraves" /> | monuments = | residence = | nationality = [[United States|American]] | other_names = | citizenship = | education = graduated from the Medical College of Wisconsin in 1975, and did research at University of Texas Department of Pharmacology<ref name="harpersBloodSpore" />{{rp|6}} | alma_mater = | occupation = | years_active = | era = | employer = | organization = | agent = <!-- Discouraged in most cases, specifically when promotio