| module = {{Infobox scientist | embed = yes | fields = [[Mycology]] | workplaces = Herbal Medicine Research Foundation, San Antonio, TX, and a [[Motorhome|Winnebago]] laboratory<ref name="harpersBloodSpore" />{{rp|10}} | patrons = | thesis_title = | thesis_url = <!--(or | thesis1_url = and | thesis2_url = )--> | thesis_year = | doctoral_advisor = <!--(or | doctoral_advisors = )--> | academic_advisors = {{plainlist| * [[Gastón Guzmán]] * [[Paul Stamets]] }} | doctoral_students = | notable_students = | known_for = | influences = | influenced = | author_abbrev_bot = | author_abbrev_zoo =
