{{ Infobox attraction | name = Monsanto House of the Future | logo = <!--Use ONLY the filename, not a full [[Image:]] link--> | logo_width = <!--Default is 250px--> | image = Monsanto Plastics Home of the Future, Disneyland, 1958 (15364290924).jpg | imagedimensions = <!--Default is 250px--> | caption = | location = Disneyland | section = [[Tomorrowland]] | status = Closed | cost = | soft_opened = June 11, 1957 | opened = June 12, 1957 | closed = December 1967 | previousattraction = <!--If there is an appropriate article, this should be linked.--> | replacement = Alpine Gardens | coordinates = {{coord|33|48|45|N|117|55|06|W|display=title,inline}} | type = Walkthrough attraction | manufacturer = <!--Must not be linked.--> | designer = Marvin Goody & Richard Hamilton<!--Should be linked.--> | model = | theme = Futuristic House set in 1986 | music = | height_ft = <!--Must be expressed in feet and may contain only numeric characters.--> | height_m = <!--Must be expressed in metres and may contain only numeric characters.--> | drop_ft = <!--Must be expressed in feet and may contain only numeric characters.--> | drop_m = <!--Must be expressed in metres and may contain only numeric characters.--> | length_ft = <!--Must be expressed in feet and may contain only numeric characters.--> | length_m = <!--Must be expressed in metres and may contain only numeric characters.--> | speed_mph = <!--Must be expressed in miles per hour and may contain only numeric characters.--> | speed_km/h = <!--Must be expressed in kilometres per hour and may contain only numeric characters.--> | sitearea_sqft = 1,280<!--Must be expressed in square feet and may contain only numeric characters.--> | sitearea_sqm = <!--Must be expressed in square metres and may contain only numeric characters.--> | gforce = | capacity = <!--Use only one of vehicles, rows, participants, or audience--> | vehicle_type = | vehicles = | riders_per_vehicle = <!--Only use if vehicles has been specified.--> | rows = | riders_p