{{Infobox sailor | name = Herman von Adlerberg | image = | caption = | birth_name = Герман фон Адлерберг | fullname = Herman von Adlerberg | nickname = | nationality = Russian | birth_date = | birth_place = | death_date = | death_place = | height = | classes = [[8 Metre (keelboat)|8 Metre]] | club = | medaltemplates-title = Competition record | medaltemplates = {{MedalSport | [[Sailing (sport)|Sailing]] }} {{MedalCountry | {{RUS}} }} {{MedalCompetition | [[Olympic Games]] [[File:Olympic rings without rims.svg|50px]] }} {{CompetitionRecordFifth | [[Sailing at the 1912 Summer Olympics – 8 Metre|1912 Nynäshamn]] | [[8 Metre (keelboat)|8 Metre]] }} | show-medals = yes | updated = 2014-02-03 }}
