{{Japanese name|Abe}} {{Infobox person | honorific_prefix = | name = Abe no Hirafu | honorific_suffix = | image = Abe_no_Hirafu.JPG | image_upright = | image_size = <!-- DISCOURAGED per WP:IMGSIZE. Use image_upright. --> | alt = | caption = | native_name = {{nihongo|阿倍 比羅夫}} | native_name_lang = | pronunciation = | birth_name = <!-- only use if different from name above --> | birth_date = unknown | birth_place = | baptised = <!-- will not display if birth_date is entered --> | disappeared_date = <!-- {{disappeared date and age|YYYY|MM|DD|YYYY|MM|DD}} (disappeared date then birth date) --> | disappeared_place = | disappeared_status = | death_date = unknown | death_place = | death_cause = | body_discovered = | resting_place = | resting_place_coordinates = <!-- {{coord|LAT|LONG|type:landmark|display=inline}} --> | burial_place = <!-- may be used instead of resting_place and resting_place_coordinates (displays "Burial place" as label) --> | burial_coordinates = <!-- {{coord|LAT|LONG|type:landmark|display=inline}} --> | monuments = | residence = | nationality = | other_names = | citizenship = | education = | alma_mater = | occupation = | years_active = 645-664 | era = [[Asuka period]] | employer = | organization = | agent = <!-- Discouraged in most cases, specifically when promotional, and requiring a reliable source --> | known_for = | notable_works = <!-- produces label "Notable work"; may be overridden by |credits=, which produces label "Notable credit(s)"; or by |works=, which produces label "Works" --> | style = | home_town = | salary = | net_worth = <!-- Net worth should be supported with a citation from a reliable source --> | height = <!-- "X cm", "X m" or "X ft Y in" plus optional reference (conversions are automatic) --> | weight = <!-- "X kg", "X lb" or "X st Y lb" plus optional reference (conversions are automatic) --> | television = | title = | term = | predecessor = | successor = | party = | movement = | opponents = | boards = | criminal_charge = <!-- Crimi