{{redirect|Abgar}} {{Infobox royalty | name = Abgar V of Edessa | title = Ruler of the kingdom of Osroene | titletext = | more = | type = | image = Abgarwithimageofedessa10thcentury.jpg | image_size = 250px | alt = | caption = [[Icon]] of Abgar holding the ''[[mandylion]]'', the image of Christ ([[encaustic painting|encaustic]], 10th century, [[Saint Catherine's Monastery]], [[Mount Sinai]]). | succession = | moretext = | reign = | reign-type = | coronation = | cor-type = | predecessor = | pre-type = | successor = | suc-type = | regent = | reg-type = | succession1 = | moretext1 = | reign1 = | reign-type1 = | coronation1 = | cor-type1 = | predecessor1 = | pre-type1 = | successor1 = | suc-type1 = | regent1 = | reg-type1 = | succession2 = | moretext2 = | reign2 = | reign-type2 = | coronation2 = | cor-type2 = | predecessor2 = | pre-type2 = | successor2 = | suc-type2 = | regent2 = | reg-type2 = | succession3 = | moretext3 = | reign3 = | reign-type3 = | coronation3 = | cor-type3 = | predecessor3 = | pre-type3 = | successor3 = | suc-type3 = | regent3 = | reg-type3 = | birth_name = | birth_date = <!-- {{birth date and age|YYYY|MM|DD|df=y}} --> | birth_place = | death_date = c. 40 | death_place = | burial_date = | burial_place = | spouse = [[Helena of Adiabene]] | spouse-type = Spouse | consort = Yes | issue = <!--list children in order of birth--> | issue-link = | issue-pipe = | issue-type = | full name = | era name = | era dates = | regnal name = | posthumous name = | temple name = | house = | house-type = | father = | mother = }} {{Infobox saint | name = Saint Abgar | image = | imagesize = | alt = | caption = | titles = | birth_name = | birth_date = | birth_place = | home_town = | residence = | death_date = | death_place = | venerated_in = Eastern Orthodox Church, Syrian Orthodox Church, Armenian Apostolic Church | beatified_date = | beatified_place = | beatified_by = | canonized_date = | canonized_place = | canonized_by = | major_shrine = | feast_day = | attributes = | p