| author = [[Norman O. Brown]]
| cover_artist = Harry Zelenko
| country = United States
| language = English
| subject = [[Sigmund Freud]]
| publisher = [[Wesleyan University Press]]
| pub_date = 1959
| media_type = Print ([[Hardcover]] and [[Paperback]])
| pages = 366
| isbn = 0-8195-5148-1
| preceded_by =
| followed_by = [[Love's Body]]
