{{Infobox person | honorific_prefix = | name = Luis Manuel Medina<!-- include middle initial, if not specified in birth_name --> | honorific_suffix = | image = <!-- filename only, no "File:" or "Image:" prefix, and no enclosing [[brackets]] --> | image_upright = | image_size = <!-- DISCOURAGED per WP:IMGSIZE. Use image_upright. --> | alt = | caption = | native_name = | native_name_lang = | pronunciation = | birth_name = <!-- only use if different from name above --> | birth_date = March 31, 1968 | birth_place = Consuelo, San Pedro de Macorís Province, Dominican Republic | baptised = <!-- will not display if birth_date is entered --> | disappeared_date = <!-- {{disappeared date and age|YYYY|MM|DD|YYYY|MM|DD}} (disappeared date then birth date) --> | disappeared_place = | disappeared_status = | death_date = February 14, 2017 (age 49) <!-- {{Death date and age|YYYY|MM|DD|YYYY|MM|DD}} (death date then birth date) --> | death_place = San Pedro de Macorís, San Pedro de Macorís Province, Dominican Republic | death_cause = Fatal gunshot wound | body_discovered = 103.5 FM Radio San Pedro de Macoris | resting_place = Consuelo, San Pedro de Macorís Province, Dominican Republic | resting_place_coordinates = 18.5593° N by 69.2967° W <!-- {{coord|LAT|LONG|type:landmark|display=inline}} --> | burial_place = <!-- may be used instead of resting_place and resting_place_coordinates (displays "Burial place" as label) --> | burial_coordinates = <!-- {{coord|LAT|LONG|type:landmark|display=inline}} --> | monuments = | residence = San Pedro de Macorís Province, Dominican Republic | nationality = Dominican Republic | other_names = | citizenship = | education = Universidad Central del Este | alma_mater = | occupation = Broadcast journalist, host and sports announcer | years_active = 25 years | era = | employer = 103.5 FM HICC, as well as Estrellas Orientales | organization = | agent = <!-- Discouraged in most cases, specifically when promotional, and requiring a reliable source --> | known_f