| institutions = {{unbulleted list |[[University of Ljubljana]] |[[New York University]] |[[Birkbeck, University of London]]}}
| notable_ideas = [[Interpassivity]]<br>[[Subversive affirmation|Over-identification]]<br>Ideology as an [[Unconscious mind|unconscious]] fantasy that structures reality<br>Revival of [[dialectical materialism]]
| influences = {{hlist |[[Louis Althusser|Althusser]] | [[Alain Badiou|Badiou]] | [[Étienne Balibar|Balibar]]|[[G.K. Chesterton|Chesterton]] | [[Božidar Debenjak|Debenjak]] |[[Sigmund Freud|Freud]]|[[Georg Wilhelm Friedrich Hegel|Hegel]]|[[Martin Heidegger|Heidegger]]| [[Fredric Jameson|Jameson]]|[[Jacques Lacan|Lacan]]|[[Ernesto Laclau|Laclau]] |[[Karl Marx|Marx]] |[[Immanuel Kant|Kant]] |[[Jacques Rancière|Rancière]]| [[Friedrich Wilhelm Joseph Schelling|Schelling]]|   [[Carl Schmitt|Schmitt]]|[[Alenka Zupančič|Zupančič]]}}
| influenced = {{hlist |[[Jodi Dean|Dean]]|[[Mark Fisher (theorist)|Fisher]]|[[Alenka Zupančič|Zupančič]]}}
