{{Infobox scientist | name = Lock Yue Chew | image = Lockyuechew.jpg | caption = Prof. Lock Yue Chew | birth_name = | birth_date = | birth_place = | death_date = | death_place = | nationality = Singapore | fields = [[Physics]] ([[Quantum chaos|Quantum Chaos]] , [[Complex Systems]], [[Statistical Physics|Statistical and Nonlinear Physics]], [[General Relativity]]) | workplaces = [[Nanyang Technological University]] | doctoral_advisor = | academic_advisors = | doctoral_students = | notable_students = | known_for = | awards = | religion = christian | footnotes =
