{{Chinese name|[[Chen (surname)|Chen]]}} {{Infobox person | pre-nominals = | name = Chen Qimei | post-nominals = | image = Chen Qi-mei.png | image_upright = | alt = <!-- descriptive text for use by speech synthesis (text-to-speech) software --> | caption = | native_name = 陳其美 | native_name_lang = zh | pronunciation = Chén Qíměi | birth_name = <!-- only use if different from name --> | birth_date = 17 January 1878 | birth_place = [[Wuxing District|Wuxing]], [[Chekiang]], [[Qing Dynasty]] | baptised = <!-- will not display if birth_date is entered --> | disappeared_date = <!-- {{Disappeared date and age|YYYY|MM|DD|YYYY|MM|DD}} (disappeared date then birth date) --> | disappeared_place = | disappeared_status = | death_date = {{Death date and age|df=yes|1916|05|18|1878|01|17}} | death_place = [[Shanghai]] | death_cause = [[Assassination]] | body_discovered = | resting_place = | resting_place_coordinates = <!-- {{coord|LAT|LONG|type:landmark|display=inline}} --> | burial_place = <!-- may be used instead of resting_place and resting_place_coordinates (displays "Burial place" as label) --> | burial_coordinates = <!-- {{coord|LAT|LONG|type:landmark|display=inline}} --> | monuments = | residence = | nationality = [[Republic of China]] | other_names = | citizenship = | education = | alma_mater = | occupation = | years_active = | era = | employer = | organization = | agent = <!-- Discouraged in most cases, specifically when promotional, and requiring a reliable source --> | known_for = | notable_works = <!-- produces label "Notable work"; may be overridden by |credits=, which produces label "Notable credit(s)"; or by |works=, which produces label "Works"; or by |label_name=, which produces label "Label(s)" --> | style = | home_town = | salary = | net_worth = <!-- Net worth should be supported with a citation from a reliable source --> | height = <!-- "X cm", "X m" or "X ft Y in" plus optional reference (conversions are automatic) --> | weight = <!-- "X kg", "X lb" or "X st Y l