| birth_date = {{Birth date|1976|07|24|df=y}} 
| birth_place = [[Kipushi]], Haut-Katanga District, Zaire
| death_date = {{Death date and age|2008|01|16|1976|07|24|df=y}} 
| death_place = [[Mont Ngafula]], Kinshasa, DR Congo
| nationality = Congolese
