{{Use dmy dates|date=December 2017}} {{Korean name|Choe}} {{Infobox person | honorific_prefix = | name = <!-- include middle initial, if not specified in birth_name --> | honorific_suffix = | image = <!-- filename only, no "File:" or "Image:" prefix, and no enclosing [[brackets]] --> | image_upright = | image_size = <!-- Discouraged per WP:IMGSIZE; please use image_upright. --> | alt = | caption = | native_name = | native_name_lang = | pronunciation = | birth_name = <!-- only use if different from name above --> | birth_date = {{birth date and age|df=yes|1934|02|26}}<!-- for living people supply only the year with {{Birth year and age|YYYY}} unless the exact date is already widely published, as per [[WP:DOB]]. For people who have died, use {{Birth date|df=yes|YYYY|MM|DD}}. --> | birth_place = [[Hwadae County]], [[North Hamgyong Province]], Korea | baptised = <!-- will not display if birth_date is entered --> | disappeared_date = <!-- {{disappeared date and age|YYYY|MM|DD|YYYY|MM|DD}} (disappeared date then birth date) --> | disappeared_place = | disappeared_status = | death_date = <!-- {{Death date and age|df=yes|YYYY|MM|DD|YYYY|MM|DD}} (death date then birth date) --> | death_place = | death_cause = | body_discovered = | resting_place = | resting_place_coordinates = <!-- {{coord|LAT|LONG|type:landmark|display=inline}} --> | burial_place = <!-- may be used instead of resting_place and resting_place_coordinates (displays "Burial place" as label) --> | burial_coordinates = <!-- {{coord|LAT|LONG|type:landmark|display=inline}} --> | monuments = | residence = | nationality = North Korean | other_names = | citizenship = | education = Baccalaureate, [[Kim Hyong-jik Teachers' College]] (1954); [[Red Flag Mangyongdae Revolutionary School]] | alma_mater = | occupation = [[Film director]], [[politician]] | years_active = 1955–present | era = | employer = [[Korea Film Studio]] | organization = | agent = <!-- Discouraged in most cases, specifically when promotional, and requirin