{{Expand Spanish|topic=geo|date=July 2011|Paracuellos de Jarama}} {{Infobox settlement <!-- See Template:Infobox settlement for additional fields and descriptions --> <!-- Basic info ----------------> |name = Paracuellos del Jarama |other_name = |native_name = <!-- for cities whose native name is not in English --> |nickname = |settlement_type = [[Municipalities of Spain|Municipality]] |motto = <!-- images and maps -----------> |image_skyline =PanorMadrid170108.jpg |imagesize = |image_caption = View of Madrid from Paracuellos. |image_flag = |flag_size = |image_seal = Coat of Arms of Paracuellos de Jarama.svg |seal_size = |image_shield = |shield_size = |image_map = |mapsize = |map_caption =Municipal location within the Community of Madrid. |pushpin_map =Spain<!-- the name of a location map as per http://en.wikipedia.org/wiki/Template:Location_map --> |pushpin_label_position =above |pushpin_mapsize = |pushpin_map_caption =Location in Spain <!-- Location ------------------> |subdivision_type = Country |subdivision_name = {{flag|Spain}} |subdivision_type1 = [[Autonomous communities of Spain|Autonomous community]] |subdivision_name1 = [[Community of Madrid]] |subdivision_type2 = [[Comarcas of Spain|Comarca]] |subdivision_name2 = [[Madrid metropolitan area]] |subdivision_type3 = |subdivision_name3 = |<!-- Politics -----------------> |government_footnotes = |government_type = |leader_title = Mayor |leader_name = Pedro Antonio Mesa Moreno |leader_title1 = <!-- for places with, say, both a mayor and a city manager --> |leader_name1 = |established_title = <!-- Settled --> |established_date = <!-- Area ---------------------> |area_magnitude = |unit_pref =Imperial <!--Enter: Imperial, if Imperial (metric) is desired--> |area_footnotes = |area_total_km2 = 43.92<!-- ALL fields dealing with a measurements are subject to automatic unit conversion--> |area_land_km2 = <!--See table @ Template:Infobox Settlement for details on automatic unit conversion--> <!-- Population -------------