{{Use mdy dates|date=November 2018}} {{Infobox settlement | name = Rancho La Brea | native_name = | native_name_lang = <!-- ISO 639-2 code e.g. "fr" for French. If more than one, use {{lang}} instead --> | settlement_type = Land grant | image_skyline = The fauna of Rancho La Brea BHL21870839.jpg | image_alt = | image_caption = The fauna of Rancho La Brea, by John C. Merriam | image_flag = | flag_alt = | image_seal = | seal_alt = | image_shield = | shield_alt = | etymology = | nickname = | motto = | image_map = | map_alt = | map_caption = | pushpin_map = | pushpin_label_position = | pushpin_map_alt = | pushpin_map_caption = | coordinates = <!-- {{Coord}} --> | coor_pinpoint = | coordinates_type = | coordinates_footnotes = | coordinates_region = | subdivision_type = Country | subdivision_name = United States | subdivision_type1 = State | subdivision_name1 = California | subdivision_type2 = County | subdivision_name2 = Los Angeles | subdivision_type3 = | subdivision_name3 = | established_title = | established_date = 1828 | founder = | seat_type = | seat = | government_footnotes = | leader_party = | leader_title = | leader_name = | unit_pref = Metric <!-- ALL fields with measurements have automatic unit conversion --> <!-- for references: use <ref> tags --> | area_footnotes = | area_urban_footnotes = <!-- <ref> </ref> --> | area_rural_footnotes = <!-- <ref> </ref> --> | area_metro_footnotes = <!-- <ref> </ref> --> | area_magnitude = <!-- <ref> </ref> --> | area_note = | area_water_percent = | area_rank = | area_blank1_title = | area_blank2_title = <!-- square kilometers --> | area_total_km2 = 17.96 | area_land_km2 = | area_water_km2 = | area_urban_km2 = | area_rural_km2 = | area_metro_km2 = | area_blank1_km2 = | area_blank2_km2 = <!-- hectares --> | area_total_ha = | area_land_ha = | area_water_ha = | area_urban_ha = | area_rural_ha = | area_metro_ha = | area_blank1_ha = | area_blank2_ha = | length_km = | width_km = | dimensions_footnotes = | elevation_footnotes = | ele