{{Infobox writer <!-- For more information see [[:Template:Infobox Writer/doc]]. --> | name = Dr. Balbir Singh | honorific_prefix = Panth Ratan | honorific_suffix = | image = | image_size = | alt = | caption = | native_name = | native_name_lang = | pseudonym = | birth_name = | birth_date = {{Birth date|df=yes|1896|12|13}} | birth_place = Amritsar, Punjab ,India; katra garba singh, Gali baba Kahan singh hakim<ref name=BS/> | death_date = {{Death date and age|df=yes| 1974 | 10 |01|1896|12|13}} | death_place = | resting_place = | occupation = Scholar | language = Punjab | nationality = Indian | ethnicity = | citizenship = | education = Ph.D. (Chemistry) | alma_mater = London University London | period = | genre = | subject = Sikh philosophy | movement = | notableworks = Nirukata Sri Guru Granth Sahib, Lambi Nadar | spouse = | partner = | children = | relatives = Bhai Vir Singh (brother) | awards =
