== Literature == * {{cite book| author = [[Басова, Надежда Александровна|Басова Н. А.]] | chapter = | chapter-url = | format = | url = http://www.dissercat.com/content/russkaya-pravoslavnaya-tserkov-v-karelii-v-1918-1941-godakh | title = Русская Православная Церковь в Карелии в 1918-1941 годах | orig-year = | agency = диссертация ... кандидата исторических наук : 07.00.02. | edition = | location = Петрозаводск| year = 2006 | publisher = [[Петрозаводский государственный университет]]| at = | volume = | issue = | pages = | page = 269 | series = | isbn = | ref = Басова}} * {{cite book| author = [[Васина, Светлана Михайловна|Васина С. М.]] | chapter = | chapter-url = | format = | url = http://dlib.rsl.ru/viewer/01002333464#?page=1 | title = Приходское духовенство Марийского края в XIX - XX вв | orig-year = | agency = диссертация ... кандидата исторических наук : 07.00.02 | edition = | location = Йошкар-Ола| year = 2003 | publisher = [[Марийский государственный университет]]| at = | volume = | issue = | pages = | page = 361 | series = | isbn = | ref = Васина}} * {{cite book| author = [[Васина, Светлана Михайловна|Васина С. М.]] | chapter = | chapter-url = | format = | url = http://cheloveknauka.com/v/17633/a?#?page=1 | title = Приходское духовенство Марийского края в XIX - XX вв | orig-year = | agency = автореферат дис. ... кандидата исторических наук : 07.00.02 | edition = | location = Йошкар-Ола| year = 2003 | publisher = [[Марийский государственный университет]]| at = | volume = | issue = | pages = | page = 21 | series = | isbn = | ref = Васина}} * {{cite book| author = [[Дрибас, Лариса Константиновна|Дрибас Л. К.]] | chapter = | chapter-url = | format = | url = http://www.dissercat.com/content/obraz-zhizni-dukhovenstva-gubernskikh-i-oblastnykh-tsentrov-vostochnoi-sibiri-vo-vtoroi-polo | title = Образ жизни духовенства губернских и областных центров Восточной Сибири во второй половине XIX века | orig-year = | agency = диссертация ... кандидата исторических наук : 07.0