w)]] |volume=23 |issue=3 |pages=195–8 | publisher = | location = | issn = | pmid = 1100446 | bibcode = | oclc =| id = | url = | language = | format = | accessdate = | laysummary = | laysource = | laydate = | quote = }} *{{cite journal |last=Skowron |first=S |authorlink= |year=1975 |title=Emil Godlewski, Jr. On the 100th anniversary of his birth (1875-1975) |journal=[[Folia Biol. (Krakow)]] |volume=23 |issue=3 |pages=193–4 | publisher = | location = | issn = | pmid = 1100445 | bibcode = | oclc =| id = | url = | language = | format = | accessdate = | laysummary = | laysource = | laydate = | quote = }}
