{{Infobox reactor | name = High-Flux Advanced Neutron Application Reactor | image = | image_size = thumb | alt = | caption = Inside the HANARO reactor hall | location_map = | location_map_width = | location_map_text = | institution = Korea Atomic Energy Research Institute | location = Daejeon, Republic of Korea | coordinates = | coordinates_ref = | type = Multi-purpose research reactor | power = 30 MW | power_therm_elec = <!--type "thermal" or "electric", for above-->
