|junction   = {{ukroadsmall|4058}}<br />{{ukroadsmall|4233}}<br />{{ukroadsmall|4093}}<br />{{ukroadsmall|473}}<br />{{ukmotorwaysmall|4}} Jnc. 34<br />{{ukroadsmall|4054}}<br />{{ukroadsmall-svg|48}}<br />{{ukroadsmall|470}}
|destinations= [[Cardiff]]
