) | Romania | 2000 |- | {{sortname|Haruhiro|Yamashita}} | Japan | 2000 |- | {{sortname|Frank|Bare, Sr.}} | United States | 1999 |- | {{sortname|Miroslav|Cerar}} | Slovenia | 1999 |- | {{sortname|Yukio|Endo}} | Japan | 1999 |- | {{sortname|Nellie|Kim}} | Soviet Union | 1999 |- | {{sortname|Eugen|Mack}} | Switzerland | 1999 |- | {{sortname|Yuri|Titov}} | Soviet Union | 1999 |- | {{sortname|Věra|Čáslavská||Caslavska, Vera}} | Czechoslovakia | 1998 |- | {{sortname|Savino|Guglielmetti}} | Italy | 1998 |- | {{sortname|Larisa|Latynina}} | Soviet Union | 1998 |- | {{sortname|Takashi|Ono}} | Japan | 1998 |- | {{sortname|Ludmilla|Tourischeva}} | Soviet Union | 1998 |- | {{sortname|Peter|Vidmar}} | United States | 1998 |- | {{sortname|Bart|Conner}} | United States | 1997 |- | {{sortname|Arthur|Gander}} | Switzerland | 1997 |- | {{sortname|Jack|Günthard}} | Switzerland | 1997 |- | {{sortname|Béla|Károlyi||Karolyi, Bela }} | Romania | 1997 |- | {{sortname|Mary Lou|Retton}} | United States | 1997 |- | {{sortname|Cathy|Rigby}} | United States | 1997 |- | {{sortname|Leon|Štukelj||Stukelj, Leon}} | Slovenia | 1997 |- | {{sortname|Masao|Takemoto}} | Japan | 1997 |- | {{sortname|Nadia|Comăneci}} | Romania | 1993 |- | {{sortname|Olga|Korbut}} | Soviet Union | 1988 |- |}
