{{Infobox |name = Indian Powerlifting Federation |bodystyle = width:20em
|titlestyle = |title = Indian Powerlifting Federation |imagestyle = |captionstyle = | image = |caption = |headerstyle = |labelstyle = width:40% |datastyle =
|header1 = | label1 = Sport | data1 = [[Powerlifting]] |header2 = | label2 = Formation | data2 = 1975 |header3 = | label3 = Type | data3 = [[Sports federation]] |header4 = | label4 = Headquarters | data4 = [[Jamshedpur]] |header5 = | label5 = Region served | data5 = India | label6 = President | data6 = Heman Putty | label7 = Website | data7 = http://www.indianpowerliftingfederation.com/
